GLuint generateChunk(glm::vec3 pos, float stride, GLuint& vertexBuffer, GLuint& normalBuffer)
{
    //std::cout << "Begin Chunk Generation" << std::endl;
    int chunkSize = MarchingChunk::chunkSize;
    int chunkSize1 = chunkSize+1;
    glMemoryBarrier(GL_ALL_BARRIER_BITS);

    //auto tStart = Clock::now();
    glBindBuffer(GL_SHADER_STORAGE_BUFFER,gridSSBO);

    glBufferData(GL_SHADER_STORAGE_BUFFER,chunkSize1*chunkSize1*chunkSize1*sizeof(float),NULL,GL_STATIC_READ);

    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,1,gridSSBO);

    glUseProgram(shader1);
    glUniform3fv(s1Pos,1,&pos[0]);
    glUniform1f(s1Stride,stride);

    glDispatchCompute(1+chunkSize/8,1+chunkSize/8,1+chunkSize/8);
    //glMemoryBarrier(GL_SHADER_STORAGE_BARRIER_BIT | GL_ATOMIC_COUNTER_BARRIER_BIT);
    glMemoryBarrier(GL_ALL_BARRIER_BITS);
    //glFinish();
//    ///DEBUG BEGINS HERE
//    GLint bufferSize;
//    glGetBufferParameteriv(GL_SHADER_STORAGE_BUFFER,GL_BUFFER_SIZE,&bufferSize);
//
//    //float myGridTest;
//    //glGetBufferSubData(GL_SHADER_STORAGE_BUFFER,(128*128*128-1)*sizeof(float),sizeof(float),&myGridTest);
//    //std::cout << "A grid value:" << myGridTest<<std::endl;
//    std::cout << "Stage 1 Complete" << std::endl;

    ///END OF STAGE 1: ALL GRID POINTS GENERATED SUCCESSFULLY!
    //float T = std::chrono::duration_cast<std::chrono::nanoseconds>(Clock::now()-tStart).count();
    //std::cout << "Stage 1:" << T <<std::endl;


    ///STAGE 2 BEGINS
    //tStart = Clock::now();
    GLuint zero = 0;

    glUseProgram(shader2);
    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,1,gridSSBO);


    glBindBufferBase(GL_ATOMIC_COUNTER_BUFFER,2,marchableCounter);
    glBufferData(GL_ATOMIC_COUNTER_BUFFER,sizeof(GLuint),&zero,GL_DYNAMIC_READ);

    glBindBufferBase(GL_ATOMIC_COUNTER_BUFFER,3,pointCounter);
    glBufferData(GL_ATOMIC_COUNTER_BUFFER,sizeof(GLuint),&zero,GL_DYNAMIC_READ);

    glBindBuffer(GL_SHADER_STORAGE_BUFFER,marchableList);
    //std::cout << "Buffer Size attempting to set: " << 512*512*512*sizeof(glm::uvec4) << std::endl;
    glBufferData(GL_SHADER_STORAGE_BUFFER,chunkSize*chunkSize*chunkSize*sizeof(glm::uvec4),NULL,GL_DYNAMIC_READ);
    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,4,marchableList);

    glDispatchCompute(1+chunkSize/8,1+chunkSize/8,1+chunkSize/8);
    //glMemoryBarrier(GL_SHADER_STORAGE_BARRIER_BIT | GL_ATOMIC_COUNTER_BARRIER_BIT);
    glMemoryBarrier(GL_ALL_BARRIER_BITS);
    //glFinish();
    //T = std::chrono::duration_cast<std::chrono::nanoseconds>(Clock::now()-tStart).count();
    //std::cout << "Stage 2:" << T <<std::endl;


    //tStart = Clock::now();
    GLuint marchCount;
    glBindBuffer(GL_ATOMIC_COUNTER_BUFFER,marchableCounter);
    glGetBufferSubData(GL_ATOMIC_COUNTER_BUFFER,0,sizeof(GLuint),&marchCount);

    //glm::uvec4 marchableSample;
    //glBindBuffer(GL_SHADER_STORAGE_BUFFER,GPUMarchingCubes::marchableList);
    //GLint szTest;
    //glGetBufferParameteriv(GL_SHADER_STORAGE_BUFFER,GL_BUFFER_SIZE,&szTest);
    //std::cout << "Marchable Size: " << szTest << std::endl;
    //glGetBufferSubData(GL_SHADER_STORAGE_BUFFER,(0)*sizeof(glm::uvec4),sizeof(glm::uvec4),&marchableSample);
    //std::cout << "Marchable Count: " << marchCount << std::endl;
    //std::cout << "Marchable Sample: "<<marchableSample.x << "|" << marchableSample.y << "|" << marchableSample.z << "|" << marchableSample.w << std::endl;




//    std::cout << "Stage 2 Complete" << std::endl;

    GLuint pointCount = 0;
    glBindBuffer(GL_ATOMIC_COUNTER_BUFFER,pointCounter);
    glGetBufferSubData(GL_ATOMIC_COUNTER_BUFFER,0,sizeof(GLuint),&pointCount);
    //std::cout << "Point Count: " << pointCount << std::endl;
    //std::cout << "PC: "<< pointCount << "MC:" << marchCount << std::endl;
    //std::cout << "Average Points Per Cube:" << (float)pointCount/marchCount << std::endl;
    int jobSize = 256;
    int jobCount = marchCount/jobSize;
    if (marchCount%jobSize != 0)
    {
        jobCount++;

    }
    //std::cout << "JC:" << jobCount << std::endl;
    glUseProgram(shader3);
    glUniform3fv(s3Pos,1,&pos[0]);
    glUniform1f(s3Stride,stride);
    GLuint marchCountUniform = glGetUniformLocation(shader3,"marchableCount");
    glUniform1ui(marchCountUniform,marchCount);
    glBindBufferBase(GL_ATOMIC_COUNTER_BUFFER,7,triangleCounter);
    glBufferData(GL_ATOMIC_COUNTER_BUFFER,sizeof(GLuint),&zero,GL_DYNAMIC_DRAW);

    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,5,vertexBuffer);
//    std::cout << "Attempting to create buffer with size: " << pointCount * sizeof(glm::vec4) << std::endl;
    glBufferData(GL_SHADER_STORAGE_BUFFER,pointCount * sizeof(glm::vec4),NULL,GL_DYNAMIC_DRAW);
//    GLint sz;
//    glGetBufferParameteriv(GL_SHADER_STORAGE_BUFFER,GL_BUFFER_SIZE,&sz);
//    std::cout << "Resulting size: " << sz << std::endl;




    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,6,normalBuffer);
    glBufferData(GL_SHADER_STORAGE_BUFFER,pointCount*sizeof(glm::vec4),NULL,GL_DYNAMIC_DRAW);



    glDispatchCompute(jobCount,1,1);





    //glMemoryBarrier(GL_SHADER_STORAGE_BARRIER_BIT | GL_ATOMIC_COUNTER_BARRIER_BIT);
    glMemoryBarrier(GL_ALL_BARRIER_BITS);
    //glFinish();
    //T = std::chrono::duration_cast<std::chrono::nanoseconds>(Clock::now()-tStart).count();
    //std::cout << "Stage 3:" << T <<std::endl;
    //std::cout << "All Stages Complete!"<<std::endl<<std::endl;
//    std::cout << "Stage 3 Complete" << std::endl;
    //std::cout << "Uploading Buffer Data" << std::endl;
    //glBindBuffer(GL_ARRAY_BUFFER,VertexDataBuffer);
    //glBufferData(GL_ARRAY_BUFFER,vertexData.size() * sizeof(glm::vec3),&vertexData[0],GL_DYNAMIC_DRAW);




    //glBindBuffer(GL_ARRAY_BUFFER,VertexNormalBuffer);
    //glBufferData(GL_ARRAY_BUFFER,vertexNormals.size()*sizeof(glm::vec3),&vertexNormals[0],GL_DYNAMIC_DRAW);

    //std::cout << "Stage 3 complete" << std::endl;
    //glBindBufferBase(GL_SHADER_STORAGE_BUFFER,5,VertexDataBuffer);

    //GLint sz;
    //glGetBufferParameteriv(GL_SHADER_STORAGE_BUFFER,GL_BUFFER_SIZE,&sz);

    //std::cout <<"Size: "<< sz << std::endl;
    //std::cout <<"Point Count: "<< pointCount << std::endl;

//    glm::vec4* mappedvertices;
//    mappedvertices = (glm::vec4*)glMapBufferRange(GL_SHADER_STORAGE_BUFFER,0,sz,GL_MAP_READ_BIT);
//    std::cout << mappedvertices[0].x << "|" << mappedvertices[1].y << "|" << mappedvertices[213].z << std::endl;
//    glUnmapBuffer(GL_SHADER_STORAGE_BUFFER);
//    delete mappedvertices;
//
    //glBindBuffer(GL_ATOMIC_COUNTER_BUFFER,GPUMarchingCubes::triangleCounter);
    //glGetBufferSubData(GL_ATOMIC_COUNTER_BUFFER,0,sizeof(GLuint),&sz);
    //std::cout << "Triangles:" << sz << std::endl;

    //std::cout << "Triangles/Points: " << (float)sz/pointCount << std::endl;
    return pointCount;
}

void init()
{

//    std::cout << "C";

    shader1 = Util::LoadComputeShader("march1.txt",true);
    shader2 = Util::LoadComputeShader("march2.txt",true);
    shader3 = Util::LoadComputeShader("march3.txt",true);
//    std::cout << "D";
    glGenBuffers(1,&gridSSBO);
    glGenBuffers(1,&pointCounter);
    glGenBuffers(1,&marchableCounter);
    glGenBuffers(1,&marchableList);
    glGenBuffers(1,&triangleCounter);
    glGenBuffers(1,&triTableBuffer);


    s1Pos=glGetUniformLocation(shader1,"chunkPosition");
    s1Stride = glGetUniformLocation(shader1,"chunkStride");
    s3Pos=glGetUniformLocation(shader3,"chunkPosition");
    s3Stride = glGetUniformLocation(shader3,"chunkStride");
    glBindBufferBase(GL_SHADER_STORAGE_BUFFER,8,GPUMarchingCubes::triTableBuffer);
    glBufferData(GL_SHADER_STORAGE_BUFFER,4096*sizeof(int),GPUMarchingCubes::triTable,GL_DYNAMIC_DRAW);


}

